import React, {Fragment} from 'react';
import logo from './logo.svg';
import './App.css';
import fetchDataNy from "./api/fetchDataNy";

class App extends React.Component {
    state = {
        books:[]
    }

    async componentDidMount(){
        const books = await fetchDataNy();
        console.log(books)
        this.setState(books)
    }

    render() {
        const titles = this.state.books.map((item)=>item.title)

        return (
            <Fragment>
                Titles:
                {titles}
            </Fragment>
        );

    }

}

export default App;
