import React from 'react';
import {Container} from "@material-ui/core";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import {clearGoodreadsReview} from "../util/stringUtil";
import Image from "react-bootstrap/Image";
import {Row} from "react-bootstrap";

const BookContainer = (props) => {
    // a            author: "",
    //  a           book_image: "",
    //  a           description: "",
    //  a           grRating: "",
    // a            numPages: "",
    //   x          primary_isbn10: "",
    //             reviews: "",
    //  a           title: "",
    //             url: ""
    return (
        // <Grid
        //     containerrrs
        //     spacing={0}
        //     direction="column"
        //     alignItems="center"
        //     justify="center"
        //     style={{
        //         // maxHeight: '100vh',
        //         textAlign: 'center',
        //         backgroundColor: '#cfe8fc'
        //     }}
        // >
        <Container>
            <Row>
            <Image className={"bookCover"} src={props.book.book_image} roundedCircle={props.book.book_image}/>
            </Row>
            {/*<Image src="holder.js/100px250" fluid />*/}
            <div className={"coverDiv"}><Image className={"bookCover"} src={props.book.book_image} roundedCircle/></div>
            <div>title: {props.book.title}</div>
            <div>Author: {props.book.author}</div>
            <div>Pages: {props.book.numPages}</div>
            <div>Description: {props.book.description}</div>
            <div>Rating: {props.book.grRating}</div>
            <div>goodreads Url: <a href={props.book.url}>{props.book.url}</a></div>
            <div dangerouslySetInnerHTML={{__html: clearGoodreadsReview(props.book.reviews)}}/>
        </Container>
        // </Grid>
    );
};

export default BookContainer;
