import React from 'react';

const Header = ({handler, current}) => {
    return (
        <div>
            I want a
            <select
                name={"seBooks"}
                id={"seBooks"}
                value={current}
                onBlur={(e)=> {
                    handler(e.target.value)
                    document.getElementById("seBooks").blur();
                }}>
                <option value={0}>Fiction</option>
                <option value={1}>Non-Fiction</option>
            </select>
            <input/>
            Best-selling book
        </div>
    );
};

export default Header;
