import React, {Fragment} from 'react';
import './App.css';
import fetchDataNyYear, {fetchDataNySingle} from "./api/fetchDataNyYear";
import {getRandomDate} from "./util/datesUtil";
import {fetchDataGr} from "./api/fetchDataGr";

class App extends React.Component {
    state = {
        book:{}
    }

    async componentDidMount() {
        const book = await fetchDataNySingle(getRandomDate(),0)
        this.setState({book})
        //await fetchDataGr("0316037915")
    }

    async componentDidUpdate() {
        if (this.state.book.title!==undefined){
            await fetchDataGr(this.state.book.primary_isbn10)
        }
    }

    render() {
        let display = "loading..."
        if (this.state.book.title!==undefined){
            console.log(this.state.book)
            display = this.state.book.title
        }
        // if (!this.state.book.title===undefined){
        //     console.log(`entrou`)
        //     display = this.state.book.title
        // }

        return (
            <Fragment>
                {display}
            </Fragment>
        );

    }

}

export default App;
