import React from 'react';

const Header = ({handler}) => {
    return (
        <div>
            I want a
            <select
                name={"seBooks"}
                id={"seBooks"}

                onChange={(e)=> handler(e.target.value)}>
                <option value={0}>Fiction</option>
                <option value={1}>Non-Fiction</option>
            </select>
            Best-selling book
        </div>
    );
};

export default Header;
