import axios from 'axios';

const endPoint = 'https://api.nytimes.com/svc/books/v3';
let date = '2018-01-01'//2
let fiction = `/lists/${date}/Hardcover Nonfiction.json`
const key = '?api-key=yIY9ATTla1XKYKfDb1cb9pMz4kXKMiJK'


const MyComponent = () => {
    var date = new Date("Sun May 11,2014");
    var dateString = new Date(date.getTime() - (date.getTimezoneOffset() * 60000 ))
        .toISOString()
        .split("T")[0];
    return dateString
};

const fetchDataNy = async () => {
    try {
        const {data: {results:{books}}} = await axios.get(endPoint+fiction+key);
        //console.log(books)
        return books;
    } catch (error) {
        console.log(`error ${error}`)

        return error;
    }
}

export default fetchDataNy
