import axios from 'axios';

const endPoint = 'https://api.nytimes.com/svc/books/v3';
let fiction = (date)=>{
    return `/lists/${date}/Hardcover Nonfiction.json`
}
const key = '?api-key=yIY9ATTla1XKYKfDb1cb9pMz4kXKMiJK'

/**
 *
 * @returns {today's date formatted as yyyy-mm-dd}
 */
const getTodayDataFormatted = () => {
    var date = new Date();
    var dateString = new Date(date.getTime() - (date.getTimezoneOffset() * 60000))
        .toISOString()
        .split("T")[0];
    return dateString
};

/**
 *
 * @param date date of the publication, should be in the format yyyy-mm-dd (getTodayDataFormatted() returns in this format)
 * @returns {Promise<*>}
 */
const fetchDataNy = async (date) => {
    date = getTodayDataFormatted();
    const url = endPoint + fiction(date) + key;
    console.log(url)
    try {
        const {data: {results: {books}}} = await axios.get(endPoint + fiction + key);
        //console.log(books)
        return books;
    } catch (error) {
        console.log(`error ${error}`)

        return error;
    }
}

export default fetchDataNy
