import React, {Fragment} from 'react';
import './App.css';
import fetchDataNyYear, {fetchDataNySingle} from "./api/fetchDataNyYear";
import {getRandomDate} from "./util/datesUtil";
import {fetchDataGr} from "./api/fetchDataGr";

class App extends React.Component {
    state = {
        books: [],
        book:{}

    }

    async componentDidMount() {
        const books = await fetchDataNyYear(getRandomDate(),0);
        const book = await fetchDataNySingle(getRandomDate(),0)
        this.setState({books, book})
        await fetchDataGr("0316037915")
    }

    render() {
        let display
        let randomTitle
        console.log(`oooooooooooo`)
            console.log(this.state.book)
        if (this.state.books.length){
            randomTitle = this.state.books[Math.floor(Math.random() * this.state.books.length)].title

            display = this.state.books.map((item, i) =>
                <div key={i}>{item.title}</div>)
        } else{
            display = 'loading...'
        }

        return (
            <Fragment>
                Titles: {randomTitle}<br/>
                {display}
            </Fragment>
        );

    }

}

export default App;
