import React, {Fragment} from 'react';
import './App.css';
import {fetchDataNySingle} from "./api/fetchDataNy";
import {getRandomDate} from "./util/datesUtil";
import {fetchDataGr} from "./api/fetchDataGr";
import BookContainer from "./components/BookContainer";
import Footer from "./components/Footer";
import {clearGoodreadsReview} from "./util/stringUtil";
import Header from "./components/Header";
import Loading from "./components/Loading";
import Container from "react-bootstrap/Container";
import Card, {Row} from "react-bootstrap";

class App extends React.Component {
    state = {
        loading: true,
        //0= fiction, 1=non fiction
        bookCategory: Math.floor(Math.random() * 2),
        book: {
            author: "",
            book_image: "",
            description: "",
            grRating: "",
            numPages: "",
            primary_isbn10: "",
            publisher: "",
            reviews: "",
            title: "",
            url: ""
        }
    }

    async componentDidMount() {
        await this.apiCalls();
        console.log("current state")
        console.log(this.state)
        //await fetchDataGr("0316037915")
    }

    async apiCalls() {
        console.log(`3 - state category?`)
        console.log(this.state.bookCategory)
        const bookNy = await fetchDataNySingle(getRandomDate(), this.state.bookCategory)
        const bookGr = await fetchDataGr(bookNy.primary_isbn10)
        this.setState({
            loading: false,
            book: {
                ...bookNy,
                ...bookGr
            }
        })
    }

    handleBookCategory = async (category) => {
        console.log(`1 - handler`)
        console.log(category)
        //this.state.bookCategory = category
        this.setState({
            bookCategory: category,
            loading: true
        }, () => {
            console.log("2 - current state inside handler")
            console.log(this.state)
            this.apiCalls()
        })
        //this.state.loading = true
    }

    render() {
        return (
            <Container direction="column"
                       alignItems="center"
                       justify="center"
                       style={{
                           // maxHeight: '100vh',
                           textAlign: 'center',
                           backgroundColor: '#cfe8fc'
                       }}>
                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src="data:image/svg+xml;charset=UTF-8,%3Csvg%20width%3D%22171%22%20height%3D%22180%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20171%20180%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_1744079d15f%20text%20%7B%20fill%3A%23999%3Bfont-weight%3Anormal%3Bfont-family%3A-apple-system%2CBlinkMacSystemFont%2C%26quot%3BSegoe%20UI%26quot%3B%2CRoboto%2C%26quot%3BHelvetica%20Neue%26quot%3B%2CArial%2C%26quot%3BNoto%20Sans%26quot%3B%2Csans-serif%2C%26quot%3BApple%20Color%20Emoji%26quot%3B%2C%26quot%3BSegoe%20UI%20Emoji%26quot%3B%2C%26quot%3BSegoe%20UI%20Symbol%26quot%3B%2C%26quot%3BNoto%20Color%20Emoji%26quot%3B%2C%20monospace%3Bfont-size%3A10pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_1744079d15f%22%3E%3Crect%20width%3D%22171%22%20height%3D%22180%22%20fill%3D%22%23373940%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%2260%22%20y%3D%2295.1%22%3E171x180%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E" />
                    <Card.Body>
                        <Card.Title>Card Title</Card.Title>
                        <Card.Text>
                            Some quick example text to build on the card title and make up the bulk of
                            the card's content.
                        </Card.Text>
                    </Card.Body>
                </Card>
                <Loading loading={this.state.loading} message={"Getting your book..."}/>
                <Row>
                <Header handler={this.handleBookCategory} current={this.state.bookCategory}/>
                </Row>
                <Row style={{
                    backgroundColor: 'white'
                }}>
                <BookContainer book={this.state.book}/>
                </Row>
                <Row style={{
                    backgroundColor: 'black'
                }}>
                <Footer/>
                </Row>

            </Container>
        );

    }

}

export default App;
