import React, {Fragment} from 'react';
import logo from './logo.svg';
import './App.css';
import fetchDataNy from "./api/fetchDataNy";

class App extends React.Component {
    state = {
        books: []
    }

    async componentDidMount() {
        const books = await fetchDataNy();
        this.setState({books})
    }

    render() {
        let display
        if (this.state.books.length){
            display = this.state.books.map(item =>
                <div>item.title</div>)
        } else{
            display = 'loading...'
        }

        return (
            <Fragment>
                Titles:<br/>
                {display}
            </Fragment>
        );

    }

}

export default App;
