import React, {Fragment} from 'react';
import './App.css';
import fetchDataNy from "./api/fetchDataNy";
import {getRandomDate} from "./util/datesUtil";

class App extends React.Component {
    state = {
        books: []
    }

    async componentDidMount() {
        const books = await fetchDataNy(getRandomDate(),0);
        this.setState({books})
    }

    render() {
        let display
        let randomTitle
        if (this.state.books.length){
            randomTitle = this.state.books[Math.floor(Math.random() * this.state.books.length)]
            display = this.state.books.map((item, i) =>
                <div key={i}>{item.title}</div>)
        } else{
            display = 'loading...'
        }

        return (
            <Fragment>
                Titles: {randomTitle}<br/>
                {display}
            </Fragment>
        );

    }

}

export default App;
