import React, {Fragment} from 'react';
import './App.css';
import {fetchDataNySingle} from "./api/fetchDataNy";
import {getRandomDate} from "./util/datesUtil";
import {fetchDataGr} from "./api/fetchDataGr";
import BookContainer from "./components/BookContainer";
import Footer from "./components/Footer";
import {clearGoodreadsReview} from "./util/stringUtil";
import Header from "./components/Header";
import Container from "@material-ui/core/Container";
import Loading from "./components/Loading";

class App extends React.Component {
    state = {
        loading: false,
        //0= fiction, 1=non fiction
        bookCategory: Math.floor(Math.random() * 2),
        book: {
            author: "",
            book_image: "",
            description: "",
            grRating: "",
            numPages: "",
            primary_isbn10: "",
            publisher: "",
            reviews: "",
            title: "",
            url: ""
        }
    }

    async componentDidMount() {
        const bookNy = await fetchDataNySingle(getRandomDate(), 0)
        const bookGr = await fetchDataGr(bookNy.primary_isbn10)
        this.setState({
            book: {
                ...bookNy,
                ...bookGr
            }
        })
        console.log("current state")
        console.log(this.state)
        //await fetchDataGr("0316037915")
    }

    handleBookCategory = (category) => {
        console.log(`handler`)
        console.log(category)
        //this.state.bookCategory = e.target.value
    }

    render() {
        //let innerReview;
        if (this.state.book.title !== undefined) {
            this.state.loading = false
            // display = this.state.book.title
            // innerReview = clearGoodreadsReview(this.state.book.reviews)
            // console.log(`innerReview`)
            // console.log(innerReview)
        }

        return (
            <Container direction="column"
                    alignItems="center"
                    justify="center"
                    style={{
                        // maxHeight: '100vh',
                        textAlign: 'center',
                        backgroundColor: '#cfe8fc'
                    }}>
                <Loading loading={this.state.loading} message={"Carregandoooooooooo"}/>
                <Header handler={this.handleBookCategory}/>
                <br/>
                <BookContainer book={this.state.book}/>
                <Footer/>

            </Container>
        );

    }

}

export default App;
